import React, { Component } from 'react';
import './Task.css';

export default class Task extends Component {

  constructor() {
    super();

    this.state = {
      completed: false,
      important: false
    }

    this.doneOnClick = () => {
      this.setState(({completed}) => {
        return {
          completed: !completed
        }
      });
    }
  }

  render() {
    let { state, taskText, taskDate } = this.props;
    let { completed, important } = this.state;
    let inputString =  <input type="text" className="edit" placeholder="Editing task" />;
    let nameString = '';
    if (completed) nameString += ' completed';
    if (important) nameString += ' important';
    return (
      <li className={nameString} >
        <div className="view">
          <input className="toggle" type="checkbox" onClick={this.doneOnClick} />
            <label>
              <span className="description">{ taskText }</span>
              <span className="created">created { taskDate }</span>
            </label>
            <button className="icon icon-edit"></button>
            <button className="icon icon-destroy"></button>
        </div>
        { state = 'editing'? inputString : null }
      </li>
    );
  }

};
