import React, { Component } from 'react';
import './App.css';

import Header from '../Header';
import TaskList from '../TaskList';
import Footer from '../Footer';

export default class App extends Component {

  constructor() {
    super();

    this.maxId = 1000;

    this.state = {
      dealsData: [ { key: 'a1', taskText: 'Do some deals', taskDate: '2 hours ago', taskCompleted: true, taskEditing: false },
                   { key: 'a2', taskText: 'Do other deals', taskDate: 'just now', taskCompleted: false, taskEditing: false },
                   { key: 'a3', taskText: 'Drink coffee', taskDate: 'about an hour ago', taskCompleted: false, taskEditing: true },
                   { key: 'a4', taskText: 'Play guitar', taskDate: '26 minutes ago', taskCompleted: false, taskEditing: false }
                  ]
    }

    this.deleteTask = (key) => {

      this.setState(({dealsData}) => {
        let keyIndex = dealsData.findIndex((elem)=> elem.key === key);
        return {
          dealsData: [ ...dealsData.slice(0, keyIndex), ...dealsData.slice(keyIndex+1)]
        }
      });
    }

    this.addTask = (newText) => {

      this.setState(({dealsData}) => {
        let newItem = { key: `a${this.maxId++}`,
                        taskText: newText,
                        taskDate: 'N+1 minutes ago',
                        taskCompleted: false,
                        taskEditing: false
                      };
        return {
          dealsData: [ ...dealsData, newItem ]
        }
      });

    }

    this.onDone = (key) => {
      console.log(key);
      this.setState(({dealsData}) => {
        let keyIndex = dealsData.findIndex((elem)=> elem.key === key);
        let elementDone = {...dealsData[keyIndex], taskCompleted: !dealsData[keyIndex].taskCompleted };
        return {
          dealsData: [ ...dealsData.slice(0, keyIndex), elementDone ,...dealsData.slice(keyIndex+1)]
        }
      });
    }

    this.onEdit = (key) => {
      console.log(key);
    }

  }

  render() {
    let {dealsData} = this.state;
    return (
      <section className = 'todoapp'>
        <Header onItemAdded = {this.addTask} />
        <section className = 'main'>
          <TaskList dealsData = { dealsData }
                    onDeleted = {this.deleteTask}
                    onDone = { this.onDone }
                    onEdit = { this.onEdit } />
          <Footer />
        </section>
      </section>
    );
  }
};
